mapper.delete:
delete
from app_chat_conversation
where user_id = {UserId}
  and id = {Id};

mapper.select:
select *
from app_chat_conversation
where user_id = {UserId}
order by create_time;

select *
from app_chat_message
where user_id = {UserId}
  and conversation_id = {ConversationId}
order by create_time

mapper.update:
update app_chat_conversation
set last_msg={LastMsg},
    last_model={LastModel},
    last_time={LastTime},
    picture={Picture}
where id = {Id}
